    def add_item_to_list(self, listWidget):
        # 添加列表参数创建对话框
        dialog = QDialog(parent=self)
        dialog.setWindowTitle("添加参数和描述")

        # 创建标签和编辑框
        parameter_label = QLabel("参数:")
        description_label = QLabel("描述:")
        parameter_edit = QLineEdit()
        parameter_edit.setPlaceholderText("例如：--icon")
        description_edit = QLineEdit()
        description_edit.setPlaceholderText("例如：添加ICO图片")

        # 创建确认按钮
        confirm_button = QPushButton("确认")

        # 创建垂直布局
        layout = QVBoxLayout(dialog)
        layout.addWidget(parameter_label)
        layout.addWidget(parameter_edit)
        layout.addWidget(description_label)
        layout.addWidget(description_edit)
        layout.addWidget(confirm_button)
        dialog.setLayout(layout)

        # 点击确认按钮时，将参数和描述添加到列表中
        confirm_button.clicked.connect(
            lambda: self.confirm_add_item(listWidget, parameter_edit.text(), description_edit.text(), dialog))

        # 打开对话框
        dialog.exec()

    def confirm_add_item(self, listWidget, parameter_text, description_text, dialog):
        # 确保参数和描述都不为空
        if parameter_text and description_text:
            # 格式化参数和描述，并添加到列表中
            item_text = parameter_text + "\n" + description_text
            item = QListWidgetItem()
            item.setText(item_text)
            listWidget.addItem(item)
            dialog.close()
        else:
            # 如果参数或描述为空，弹出消息框提醒用户
            QMessageBox.warning(self, "警告", "参数和描述不能为空！")

    def remove_item_from_list(self, listWidget):
        # 删除列表参数
        for item in listWidget.selectedItems():
            listWidget.takeItem(listWidget.row(item))

    def save_parameters_to_config(self, listWidget, selected_packaging):
        # 保存列表参数及其选中状态到配置文件
        parameters = []
        for index in range(listWidget.count()):
            item = listWidget.item(index)
            parameter_text = item.text().split("\n")[0]
            description_text = item.text().split("\n")[1]
            is_selected = item.isSelected()
            parameters.append((parameter_text, description_text, is_selected))

        if not parameters:
            QMessageBox.warning(self, "警告", "参数列表为空，无法保存！")
            return

        config_file = "pyinstaller_config.json" if selected_packaging == "pyinstaller" else "nuitka_config.json"
        try:
            with open(config_file, 'w') as f:
                json.dump(parameters, f)
            QMessageBox.information(self, "成功", "参数已成功保存到配置文件！")
        except Exception as e:
            QMessageBox.critical(self, "错误", f"保存参数时发生错误：{str(e)}")

    def delete_config_file(self, selected_packaging):
        # 删除配置文件
        current_directory = os.getcwd()  # 获取当前工作目录
        config_file = None

        if selected_packaging == "pyinstaller":
            config_file = os.path.join(current_directory, "pyinstaller_config.json")
        elif selected_packaging == "nuitka":
            config_file = os.path.join(current_directory, "nuitka_config.json")

        if config_file and os.path.exists(config_file):
            os.remove(config_file)
            self.debug_text_edit.append(f"已删除 {selected_packaging.capitalize()} 配置文件")
        else:
            self.debug_text_edit.append(f"{selected_packaging.capitalize()} 配置文件不存在")

    def start_packaging_thread(self, selected_packaging, dialog, listWidget):
        # 打包逻辑
        dialog.accept()
        config_file = "pyinstaller_config.json" if selected_packaging == "pyinstaller" else "nuitka_config.json"
        if os.path.exists(config_file):
            with open(config_file, 'r') as f:
                parameters = json.load(f)
            if selected_packaging == "pyinstaller":
                # 弹出对话框让用户选择是否使用虚拟环境
                reply = QMessageBox.question(dialog, '选择虚拟环境', '是否使用虚拟环境？',
                                             QMessageBox.StandardButton.Yes | QMessageBox.StandardButton.No,
                                             QMessageBox.StandardButton.No)
