    def update_debug_text(self, message):
        self.debug_text_edit.append(message)

    def get_icon_file(self, directory):
        # 获取ICO图片路径
        icon_files = [f for f in os.listdir(directory) if f.lower().endswith('.ico')]
        if icon_files:
            return os.path.join(directory, icon_files[0])
        else:
            self.debug_text_edit.append("未找到ICO图片文件")
            return None

    def select_directory(self):
        # 选择虚拟环境目录
        try:
            directory = QFileDialog.getExistingDirectory(self, "选择目录", "")
            if directory:
                self.directory_lineEdit.setText(os.path.normpath(directory))
        except Exception as e:
            self.debug_text_edit.append(f"选择目录时出错: {str(e)}")

    def select_db_file(self):
        # 打开选择的文件目录
        py_file = self.py_file_lineEdit.text()
        py_file_directory = os.path.dirname(py_file)  # 使用文件的目录作为OUTPUT
        if py_file_directory:
            try:
                subprocess.Popen(f'explorer "{os.path.normpath(py_file_directory)}"')
            except Exception as e:
                self.debug_text_edit.append(f"打开文件夹时出错: {str(e)}")
        else:
            self.debug_text_edit.append("请选择 py 或 spec 文件")

    def select_py_file(self):
        # 选择py文件
        try:
            file_path, _ = QFileDialog.getOpenFileName(self, "选择文件", "", "Python Files (*.py *.spec)")
            if file_path:
                self.py_file_lineEdit.setText(os.path.normpath(file_path))
        except Exception as e:
            self.debug_text_edit.append(f"选择文件时出错: {str(e)}")

